function AoAnyClickHoverShowChild() {
  document.querySelectorAll(".ao-any-click-hover-show-child__open").forEach(o => {
    o.addEventListener("click", () => {
      let e = o.closest(".ao-any-click-hover-show-child").parentElement.querySelectorAll(".ao-any-click-hover-show-child__child"),
          c = o.parentElement.querySelector(".ao-any-click-hover-show-child__child");
      e.forEach(o => {
        o === c ? c.classList.toggle("ao-any-click-hover-show-child_open") : o.classList.remove("ao-any-click-hover-show-child_open");
      });
    });
  });
}

export default AoAnyClickHoverShowChild;