function AoModal() {
  let o = document.querySelectorAll(".ao-modal__open"),
      e = document.querySelectorAll(".ao-modal__close");
  o.forEach(o => {
    o.addEventListener("click", o => {
      document.querySelector(`.ao-modal__scope[data-modal="${o.target.dataset.modal}"]`).classList.add("ao-modal__scope_open"), o.preventDefault();
    });
  }), e.forEach(o => {
    o.addEventListener("click", o => {
      let e;
      o.target.classList.contains("ao-modal__close") && !o.target.classList.contains("ao-modal__scope") ? (e = o.target.closest(".ao-modal__scope"), o.stopPropagation(), o.preventDefault()) : e = o.target, e.classList.remove("ao-modal__scope_open");
    });
  });
}

export default AoModal;